slides.code:
clear all; % Clear workspace
clc; % Clear command window
disp('=== Rolle’s Theorem ===');

f_a = double(subs(f_rolle, x, a_rolle)); % f(0)
f_b = double(subs(f_rolle, x, b_rolle)); % f(2)
disp(['f(', num2str(a_rolle), ') = ', num2str(f_a)]);
disp(['f(', num2str(b_rolle), ') = ', num2str(f_b)]);
if abs(f_a - f_b) < 1e-6
    disp('Condition f(a) = f(b) satisfied');
else
    disp('Warning: f(a) ≠ f(b), Rolle’s Theorem not applicable');
end

f_rolle_prime = diff(f_rolle, x); % f'(x)
disp(['First derivative: f''(x) = ', char(f_rolle_prime)]);
c_rolle = double(solve(f_rolle_prime, x)); % Solve f'(x) = 0

figure(1); hold on; grid on;
f_rolle_handle = matlabFunction(f_rolle);
x_vals = linspace(a_rolle, b_rolle, 100);
plot(x_vals, f_rolle_handle(x_vals), 'b-', 'LineWidth', 2);
plot(c_rolle, double(subs(f_rolle, x, c_rolle)), 'ro', 'MarkerFaceColor', 'r');
% Tangent line at c
y_rolle_c = double(subs(f_rolle, x, c_rolle));
x_tangent = linspace(c_rolle - 0.5, c_rolle + 0.5, 50);
y_tangent = y_rolle_c * ones(size(x_tangent));
plot(x_tangent, y_tangent, 'k--', 'LineWidth', 1.5);
xlabel('x'); ylabel('f(x)'); title('Rolle’s Theorem: Horizontal Tangent');
legend('f(x) = x^2 - 2x + 1', sprintf('c = %.2f', c_rolle), 'Tangent at c');
hold off;

disp('=== Mean Value Theorem ===');
f_mvt = x^3 - 3*x + 1;
a_mvt = 0;
b_mvt = 2;

f_mvt_prime = diff(f_mvt, x);
disp(['First derivative: f''(x) = ', char(f_mvt_prime)]);
c_mvt = double(solve(f_mvt_prime - secant_slope, x));
c_mvt = c_mvt(imag(c_mvt) == 0 & c_mvt > a_mvt & c_mvt < b_mvt);
disp(['Point(s) c where f''(c) = secant slope: ', num2str(c_mvt')]);

figure(2); hold on; grid on;
f_mvt_handle = matlabFunction(f_mvt);
x_vals = linspace(a_mvt, b_mvt, 100);
plot(x_vals, f_mvt_handle(x_vals), 'b-', 'LineWidth', 2);
% Secant line
secant = subs(f_mvt, x, a_mvt) + secant_slope * (x - a_mvt);
secant_handle = matlabFunction(secant);
plot(x_vals, secant_handle(x_vals), 'g--', 'LineWidth', 1.5);
% Tangents at c
legend_entries = {'f(x) = x^3 - 3x + 1', 'Secant Line'};
for i = 1:length(c_mvt)
    tangent_mvt = subs(f_mvt, x, c_mvt(i)) + secant_slope * (x - c_mvt(i));
    tangent_handle = matlabFunction(tangent_mvt);
    x_tangent = linspace(c_mvt(i) - 0.5, c_mvt(i) + 0.5, 50);
    plot(x_tangent, tangent_handle(x_tangent), 'r-', 'LineWidth', 1.5);
    plot(c_mvt(i), double(subs(f_mvt, x, c_mvt(i))), 'ro', 'MarkerFaceColor', 'r');
    legend_entries{end+1} = sprintf('Tangent at c = %.2f', c_mvt(i));
    legend_entries{end+1} = sprintf('c = %.2f', c_mvt(i));
end
xlabel('x'); ylabel('f(x)'); title('Mean Value Theorem: Tangent = Secant Slope');
legend(legend_entries);
hold off;
% Explanation text
disp('=== Explanation ===');
disp('Rolle’s Theorem: The red point(s) show where the tangent is horizontal (f''(c) = 0).');
disp('This happens because f(0) = f(2), so the function must "turn" somewhere.');
disp('Mean Value Theorem: The red point(s) show where the tangent slope equals the');
disp('secant slope (green dashed line). This represents instantaneous rate equaling');
disp('average rate, like a car’s speed matching its average speed over a trip.');
disp('To explore: Change f_rolle or f_mvt (e.g., to sin(x)) and re-run!');
disp('R2014b Note: Constant tangent lines (Rolle’s) are plotted as y = constant.');
