% Step 3: Compute Taylor series for each order
for i = 1:length(orders)
    n = orders(i);
    T = 0; % Initialize Taylor polynomial
    for k = 0:n
        % Compute kth derivative
        df = diff(f, k);
        % Evaluate at x = a
        df_a = subs(df, x, a);
        % Add kth term to series
        T = T + (df_a / factorial(k)) * (x - a)^k;
    end
    taylor_approx{i} = T; % Store approximation
end

% Step 5: Plot original function and Taylor approximations
figure(1); plot(x_vals, f_num(x_vals), 'k-', 'LineWidth', 2, 'DisplayName', 'sin(x)');
ylim([-5,5]);
xlabel('x'); ylabel('y'); title('sin(x)');
legend('show'); grid on;
figure(2); plot(x_vals, f_num(x_vals), 'k-', 'LineWidth', 2, 'DisplayName', 'sin(x)');
hold on;
colors = ['r', 'b', 'g', 'm', 'c', 'y'];
for i = 1:length(orders)
    T_num = matlabFunction(taylor_approx{i}); % Convert to numerical
    my_idx = find(abs(T_num(x_vals))<=5);
    plot(x_vals(my_idx), T_num(x_vals(my_idx)), 'Color', colors(i), 'LineStyle', '--', ...
        'DisplayName', sprintf('Order %d', orders(i)));
end
xlabel('x'); ylabel('y'); title('Taylor Series Approximation of sin(x) at x = 0');
legend('show'); grid on; hold off;

% Step 6: Compute and plot truncation error
figure(3); for i = 1:length(orders)
    T_num = matlabFunction(taylor_approx{i});
    error = abs(f_num(x_vals) - T_num(x_vals));
    semilogy(x_vals, error, 'Color', colors(i), ...
        'DisplayName', sprintf('Order %d', orders(i)));
    hold on;
end
xlabel('x'); ylabel('Absolute Error (log scale)'); title('Truncation Error of Taylor Series for sin(x)');
legend('show'); grid on; hold off;
