%Convert symbolic functions to numerical for plotting
f_num = matlabFunction(f);
fx_num = matlabFunction(fx);
fy_num = matlabFunction(fy);

% [xg, yg] = meshgrid(linspace(-2, 2, 20), linspace(-pi, pi, 20));
[xg, yg] = meshgrid(linspace(-2, 2, 20), linspace(-2, 2, 20));
zg = f_num(xg, yg);
fx_vars = symvar(fx); % get the list variables present in fx
fy_vars = symvar(fy); % get the list variables present in fy
if isempty(fx_vars)
    fx_vals = double(fx)*ones(size(xg));
elseif length(fx_vars)==1
    fx_vals = fx_num(xg);
else
    fx_vals = fx_num(xg,yg);
end
if isempty(fy_vars)
    fy_vals = double(fy)*ones(size(yg));
elseif length(fy_vars)==1
    fy_vals = fy_num(yg);
else
    fy_vals = fy_num(xg,yg);
end

% Plot 3D surfcace of the function
figure; surfc(xg, yg, zg);
xlabel('x'); ylabel('y'); zlabel('f(x,y)');
title(['surfcace Plot of f(x,y) = ' string(f)]);
grid on; hold off;

% Plot 2D contour with gradient vector field
figure; contour(xg, yg, zg, 15, 'k'); hold on;
quiver(xg, yg, fx_vals, fy_vals, 'b', 'LineWidth', 1.2);
xlabel('x'); ylabel('y');
title(['Contour Plot with Gradient Vector Field of f(x,y) = ' string(f)]);
grid on; legend('Contours', 'Gradient Vectors'); hold off;

% Plot 3D surfcace with gradient vectors
figure; surfc(xg, yg, zg, 'FaceAlpha', 0.8); hold on;
quiver3(xg, yg, zg, fx_vals, fy_vals, zeros(size(zg)), 'r', 'LineWidth', 1.5);
xlabel('x'); ylabel('y'); zlabel('f(x,y)');
title(['3D surfcace with Gradient Vectors of f(x,y) = ' string(f)]);
grid on; legend('surfcace', 'Gradient Vectors'); hold off;

%Plot partial derivatives as surfcaces 
 figure;
subplot(1, 2, 1); surfc(xg, yg, fx_vals);
xlabel('x'); ylabel('y'); zlabel('\partial f / \partial x');
title('Partial Derivative \partial f / \partial x'); grid on;
subplot(1, 2, 2); surfc(xg, yg, fy_vals);
xlabel('x'); ylabel('y'); zlabel('\partial f / \partial y');
title('Partial Derivative \partial f / \partial y'); grid on;

% Display symbolic results
disp('Partial derivative with respect to x:');
disp(fx);
disp('Partial derivative with respect to y:');
disp(fy);
disp('Gradient vector:');
disp(grad_f);
